// script.js

// Initialize page.js routing
page.base('/');

// Define routes
page('/', function() {
    // Implement logic for main form view
    document.getElementById('userForm').classList.remove('d-none');
    document.getElementById('aboutSection').classList.add('d-none');
});

page('/about', function() {
    // Implement logic for about section view
    document.getElementById('aboutSection').classList.remove('d-none');
    document.getElementById('userForm').classList.add('d-none');
});

// Start page.js
page();
